import React from 'react';
import {Container,Typography,Button,Grid} from '@material-ui/core';
import useStyles from './styles';

const Cart = ({cart}) => {
    const isEmpty =!cart.line-items.length;
    const classes = useStyles();
    const EmptyCart =()=>(
        <Typography variant='Subtitle1'>You have no items in shopping cart</Typography>
    );

    const FilledCart=()=>(
        <>
        <Grid container spacing={3}>
        {Cart.line_items.map((item)=>(
            <Grid item xs ={12} sm={4} key={item.id}>
                <div>item name</div>
            </Grid>
            ))}
        </Grid>
        <div className={classes.cardDetails}>
            <Typography varient ="h4">SubTotal:{cart.subTotal.formatted_with_symbol}</Typography>
            <div >
                <Button className={classes.emptyButton} size="large" type='Button' varient="contained" color='secondary'>Empty Cart</Button>
                <Button className={classes.checkout} size="large" type='Button' varient="contained" color='primary'>CheckOut</Button>
            </div>
        </div>
        </>
    )
    if(!cart.line_items) return 'Loading....';
  return (
    <Container>
        <div className={classes.toolbar}/>
        <Typography className={classes.title } varient ="h3">Your Shopping Cart</Typography> 
        {! cart.line_items.length ? <EmptyCart/> :<filledCart/>}
            </Container>
  )
}

export default Cart;
